CREATE TABLE `authDevice` (
  `id` BIGINT UNSIGNED NOT NULL AUTO_INCREMENT,`brand_id` BIGINT UNSIGNED NOT NULL,`arch_id` BIGINT UNSIGNED NOT NULL,`gpu_id` BIGINT UNSIGNED NOT NULL,`cpu_num` MEDIUMINT UNSIGNED NOT NULL COMMENT 'CPU核心数',`w` SMALLINT UNSIGNED NOT NULL COMMENT '分辨率宽',`h` SMALLINT UNSIGNED NOT NULL COMMENT '分辨率高',`dpi` TINYINT UNSIGNED NOT NULL,PRIMARY KEY (`id`),UNIQUE KEY `uq_hardware` (`brand_id`,`arch_id`,`gpu_id`,`cpu_num`,`w`,`h`,`dpi`)
);